DumpSystemOverview()
{
    local systemVersion=$(CheckOsVersion)

    oIFS="$IFS"; IFS=$'\r\n'
    tmpChispetArr=()
    tmpVendorArr=()
    tmpDeviceArr=()
    tmpRevisionArr=()
    tmpChipsetArr=( $( /usr/sbin/system_profiler SPDisplaysDataType | grep "Chipset Model:" ))
    tmpVendorArr=( $( /usr/sbin/system_profiler SPDisplaysDataType | grep "Vendor:" ))
    tmpDeviceArr=( $( /usr/sbin/system_profiler SPDisplaysDataType | grep "Device ID:" ))
    tmpRevisionArr=( $( /usr/sbin/system_profiler SPDisplaysDataType | grep "Revision ID:" ))
    # Read tmp array for video and compile in to am html string for the report overview.
    if [ ${#tmpChipsetArr[@]} -gt 0 ]; then
        for (( c=0; c<${#tmpChipsetArr[@]}; c++ ))
        do
            tmpChipset="${tmpChipsetArr[$c]##*: }"
            tmpVendor="${tmpVendorArr[$c]##*(0x}"; tmpVendor="${tmpVendor%*)}"
            tmpDevice="${tmpDeviceArr[$c]##* 0x}"
            tmpRevision="${tmpRevisionArr[$c]##*0x}"
            [[ c -gt 0 ]] && macGraphics="${macGraphics}<br />"
            macGraphics="${macGraphics}"$( printf '%s\n\r' "Video: ${tmpChipset} <span class=\"text_overview_device\">[${tmpVendor}:${tmpDevice}] Rev.[${tmpRevision}]</span>" )
        done
    else
        macGraphics="${tmp[@]}"
    fi
    IFS="$oIFS"

    local macModel=$( /usr/sbin/system_profiler SPHardwareDataType 2>/dev/null | grep "Model Identifier:" )
    macModel="${macModel##*: }"
    
    #local macOsVer=$( /usr/sbin/system_profiler SPSoftwareDataType | grep "System Version" )
    #if [[ "$macOsVer" == *macOS* ]]; then
    #  macOsVer="${macOsVer##*OS }"
    #else
    #  macOsVer="${macOsVer##*X }"
    #fi
    
    local macMemory=$( /usr/sbin/system_profiler SPHardwareDataType 2>/dev/null | grep "Memory:" )
    macMemory="${macMemory##*: }"

    # Get memory type and speed.
    local memType=$( /usr/sbin/system_profiler SPMemoryDataType | grep Type | grep -v 'Empty' | tail -n1 )
    memType="${memType##*: }"
    local memSpeed=$( /usr/sbin/system_profiler SPMemoryDataType | grep Speed | grep -v 'Empty' | tail -n1 )
    memSpeed="${memSpeed##*: }"

    if [ $systemVersion -eq 8 ] || [ $systemVersion -eq 9 ]; then
        if [ -f "$gDumpFolderSysProf/System-Profiler.txt" ]; then
            macCpu=$( fgrep "Processor Name:" "$gDumpFolderSysProf/System-Profiler.txt" )
        else
            macCpu=$( sysctl -a | grep "cpu.brand_string" )
        fi
    else
        macCpu=$( sysctl -a | grep "cpu.brand_string" )
    fi
    local macCpu="${macCpu##*: }"
    local timeStamp=$( date +"%A %d %B %Y" )

    # There's only space for 4 lines in the report header for audio and graphics
    # Sometimes the AudioCodec can report more than one line.
    # For Example:
    # Cirrus Logic CS4206
    # ATI R6xx HDMI
    #
    # So, in the case of multiple devices remove the line breaks and add a slash to separate them.
    #gCodecID=$( echo "$gCodecID" | tr "\n" "/"  | sed 's/.$//' )
    #macGraphics=$( echo "$macGraphics" | tr "\n" "/"  | sed 's/.$//' )

    WriteHtmlSectionAnchorToFile "aTop"
    WriteHtmlOverviewToFile "$timeStamp" "$OSPRODUCTVERSION ($OSBUILDVERSION)" "$macModel" "$macCpu" "$macGraphics" "${macMemory} ${memSpeed} ${memType}" "$gCodecID" "$gSic"
}

